import locationMarker from '../icons/location_marker.svg';
import '../styles/TravelCard.css';

export default function TravelCard(props) {
    return (
        <>
            <CardInfo {...props}/>
        </>
    );
}

function CardInfo(props) {

    Date.prototype.toString = function(){
        const month = this.toLocaleString('default', { month: 'long' });
        return `${this.getDay()} ${month.slice(0,3)}/${this.getFullYear()}`;
    };

    let dateString = props.from.toString() + ' - ' + props.to.toString();

    return (
        <>
            <img src={locationMarker} alt='' className='CardHeader__locationMarker'></img>
            <p className='CardHeader__location'>{props.location}</p>
            <a className='CardHeader__link'>View on google maps</a>
            <h2 className='CardHeader__title'>{props.title}</h2>
            <p>{dateString}</p>
        </>
    );
}
